var EvalSingle=function(context,exp){var res;with(context)res=eval(exp);return res};function EvalMulti(context,str){for(var res="",values=str.match(/\{\{(.*?)\}\}/g),start=0,end=0,i=0;i<values.length;i++){with(end=str.indexOf(values[i]),context)res+=str.substring(start,end)+eval(values[i].substring(2,values[i].length-2));start=end+values[i].length}return res}